import React, { useState } from "react";
import DropdownPicker from "./DropdownPicker";
import MonthPicker from "./MonthPicker";
import DatePicker from "./DatePicker";
import YearPicker from "./YearPicker";

type optionalDatePickerProps = {};

const OptionalDatePicker: React.FC<optionalDatePickerProps> = (props:optionalDatePickerProps) => {

  const [month, setMonth] = useState<number>();
  const handleMonthChange = (event:any) => {
    setMonth(event.target.value);
  }

  const [date, setDate] = useState<number>();
  const handleDateChange = (event:any) => {
    setDate(event.target.value);
  }

  const [year, setYear] = useState<number>();
  const handleYearChange = (event:any) => {
    setYear(event.target.value);
  }

  let numberDaysInMonth;
  let isLeapYear = year && (year % 400 == 0 || year % 100 != 0 && year % 4 == 0);
  if (month == 4 || month == 6 || month == 9 || month == 11){
    numberDaysInMonth = 30;
  } else if (month == 2 && isLeapYear){
    numberDaysInMonth = 29;
  } else if (month == 2){

  } else {
    numberDaysInMonth = 31;
  }

  return (
    <div>
      <MonthPicker
        selectedValue={month}
        onChange={handleMonthChange}
      />
      <DatePicker 
        selectedValue={date} 
        onChange={handleDateChange} 
        numberDaysInMonth={numberDaysInMonth} 
      />
      <YearPicker 
        selectedValue={year} 
        onChange={handleYearChange} 
        inclusiveMinYearBound={2000} 
        inclusiveMaxYearBound={2024} 
      />
    </div>
  )
}

export default OptionalDatePicker;
