import React, { useState } from "react";
import { QueryClient, useMutation, useQueryClient } from "react-query";
import { postNewEntry } from "../services/serverConnection";
import { DataEntry } from "../../../types";
import axios from "axios";

type newEntryFormProps = {};

const API_BASE_URL = 'http://localhost:3001';

// Create an instance of Axios with the base URL configured
const api = axios.create({
  baseURL: API_BASE_URL,
});

const NewEntryForm: React.FC<newEntryFormProps> = (props:newEntryFormProps) => {

  const [inputValue, setInputValue] = useState('');

  const mutation = useMutation(
    async (payload: any) => {
      const response = await api.post(`/newEntry`, payload); // Adjust API endpoint as per your backend
      return;
    },
    {
      onSuccess: () => {
        // Invalidating and refetching data after mutation succeeds
        // This assumes you have a query key like 'yourQueryKey' defined for the data you want to invalidate and refetch
        clientInformation.invalidateQueries('entries'); // Replace 'yourQueryKey' with your actual query key
      },
    }
  );

  const handleSubmit = (e:any) => {
    e.preventDefault();
    // Call mutate to trigger the mutation function defined above
    mutation.mutate({ data: inputValue });
    setInputValue(''); // Clear input after submission
  };

  const handleInputChange = (e:any) => {
    setInputValue(e.target.value);
  };

  return (
    <div>
      <h2>Update Form</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="updateInput">Update Value:</label>
        <input
          type="text"
          id="updateInput"
          value={inputValue}
          onChange={handleInputChange}
          required
        />
        <button type="submit" disabled={mutation.isLoading}>
          {mutation.isLoading ? 'Updating...' : 'Submit'}
        </button>
      </form>
    </div>
  );
}

export default NewEntryForm;
