import React, { useState } from "react";
import DropdownPicker from "./DropdownPicker";

type YearPickerProps = {
  selectedValue: any,
  onChange: any,
  inclusiveMinYearBound: number,
  inclusiveMaxYearBound: number
}

const YearPicker: React.FC<YearPickerProps> = ({selectedValue, onChange, inclusiveMinYearBound, inclusiveMaxYearBound}) => {

  const yearOptions = [];
  if(includeNoValueOption) yearOptions.push({ value: null, label: '-'});
  for (let i = inclusiveMinYearBound; i <= inclusiveMaxYearBound; i++){
    yearOptions.push({value: i, label: i}); 
  }

  return(
    <div>
      <DropdownPicker
        options={yearOptions}
        selectedValue={selectedValue}
        onChange={onChange}
      />
    </div>
  )
}

export default YearPicker;
