  tagOptions: TagObject[] | undefined; //TODO: do we want to allow undefined to be a valid inout for TagInput components, or should we handle that before and never have it reach the component

}

const TagInput: React.FC<TagInputProps> = ({tagOptions}) => {

  if (!tagOptions) return (<div>No data found</div>)
    if (!data) return 

  let options = tagOptions?.map()

  return(
    <div>
        <CreatableSelect 
          isMulti
          options={options}
        />
    </div>
  )
} 

export default TagInput;
