export function intToShortMonth(index:number){
  if(index < 1 || index > 12){
    return 'Invalid Index for Short Month';
  }

  let shortMonthsArray = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];

  return shortMonthsArray[index - 1];
}
