import axios from 'axios';
import { NewDataEntry } from '../../../types';

const API_BASE_URL = 'http://localhost:3001';

// Create an instance of Axios with the base URL configured
const api = axios.create({
  baseURL: API_BASE_URL,
});

export async function testServerConnection(): Promise<void> {
  try {
    const response = await axios.get(`http://localhost:3001/entry/`)
  } catch (error) {
    console.error('Error sending data to server:', error);
    throw error; // Optionally re-throw the error for the caller to handle
  }
}

export async function postNewEntry (payload:NewDataEntry): Promise<void> {
  try {
    console.log("Attempted payload: ", payload);

    const response = await axios.post(`http://localhost:3001/newEntry`, payload);

    // Handle the response if needed
    console.log('Response from server:', response.data);
  } catch (error) {
    // Handle errors
    console.error('Error sending data to server:', error);
    throw error; // Optionally re-throw the error for the caller to handle
  }
};
