// Form.tsx

import React, { FormHTMLAttributes, useState } from 'react';
import SubmitFormButton from './SubmitFormButton';
import {NewDataEntry} from  '../../../types';

// Define props for the Form component
type props = FormHTMLAttributes<HTMLFormElement> & {

};

// Reusable Form component
const NewEntryForm: React.FC<props> = ({ children, ...rest }) => {
  const [formData, setFormData] = useState<NewDataEntry>({
    message: ''
  });

  async function handleSubmit (event: React.FormEvent<HTMLFormElement>) {
    event.preventDefault();

    try {
      const response = await fetch('/api/user', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });

      if (!response.ok) {
        throw new Error('Failed to submit form');
      }

      // Reset the form after successful submission
      setFormData({
        message: ''
      });

      alert('Form submitted successfully');
    } catch (error:any) {
      console.error('Error submitting form:', error.message);
      alert('Failed to submit form. Please try again later.');
    }
  };

  function handleChange (event: React.ChangeEvent<HTMLInputElement>) {
    const { name, value } = event.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
    console.log(formData);
  };

  return (
    <form onSubmit={handleSubmit} >
      <input type="text" onChange={handleChange}/>
      {children}
      <SubmitFormButton></SubmitFormButton>
    </form>
  );
};

export default NewEntryForm;
