import React, { useEffect, useState } from 'react';
import NewEntryForm from './NewEntryForm';
import { DataEntry } from '../../../types';
import Entry from './Entry';
import { getAllEntries } from '../services/serverConnection';

type EntriesProps = {}

const Entries: React.FC<EntriesProps> = () => {
  const [entries, setEntries] = useState<DataEntry[]>([]);

    //After the render of the app, fetch the entries stores on the servers
    useEffect(() => {
      const fetchData = async () => {
        try {
          const entries = await getAllEntries();
          setEntries(entries);
        } catch (error) {
          console.error('Error fetching entries:', error);
        }
      };

      fetchData();
    }, []); // Empty dependency array ensures effect runs only once after initial render

  return (
    <div>
      <NewEntryForm />
      <div>
        {
          entries.map((entry, index) => (
          <Entry key={index} DataEntry={entry} />
        ))}
      </div>
    </div>
  );
};

export default Entries;
