import React, { useState } from "react";
import { QueryClient, useMutation, useQueryClient } from "react-query";
import { postNewEntry } from "../services/serverConnection";
import { DataEntry, NewDataEntry } from "../../../types";
import axios from "axios";

type newEntryFormProps = {};

const NewEntryForm: React.FC<newEntryFormProps> = (props:newEntryFormProps) => {

  const [inputValue, setInputValue] = useState<string>('');
  const queryClient = useQueryClient();

  const mutation = useMutation<void, unknown, >(
    postNewEntry,
    {
      onSuccess: () => {
        // Invalidating and refetching data after mutation succeeds (for the key of entries)
        queryClient.invalidateQueries('entries');
      },
    }
  );

  const handleSubmit = (e:any) => {
    e.preventDefault();

    let inputConvertedToNewEntry : NewDataEntry = { message: inputValue }
    // Call mutate to trigger the mutation function defined above
    mutation.mutate(inputConvertedToNewEntry);
    setInputValue(''); // Clear input after submission
  };

  const handleInputChange = (e:any) => {
    setInputValue(e.target.value);
  };

  return (
    <div>
      <h1>Update Form</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="updateInput">Update Value:</label>
        <input
          type="text"
          id="updateInput"
          value={inputValue}
          onChange={handleInputChange}
          required
        />
        <button type="submit" disabled={mutation.isLoading}>
          {mutation.isLoading ? 'Updating...' : 'Submit'}
        </button>
      </form>
    </div>
  );
}

export default NewEntryForm;
