import React, { useState } from "react";
import DropdownPicker from "./DropdownPicker";
import { intToShortMonth } from "../utils/helpers";

type MonthPickerProps = {
  selectedValue: any,
  onChange: any
}

const MonthPicker: React.FC<MonthPickerProps> = ({selectedValue, onChange}) => {

  // const monthOptions = [
  //   { value: 1, label: 'Jan' },
  //   { value: 2, label: 'Feb' },
  //   { value: 3, label: 'Mar' },
  //   { value: 4, label: 'Apr' },
  //   { value: 5, label: 'May' },
  //   { value: 6, label: 'Jun' },
  //   { value: 7, label: 'Jul' },
  //   { value: 8, label: 'Aug' },
  //   { value: 9, label: 'Sep' },
  //   { value: 10, label: 'Oct' },
  //   { value: 11, label: 'Nov' },
  //   { value: 12, label: 'Dec' },
  // ];

  const monthOptions:any[] = [];
  for(let i = 1; i <= 12; i++){
    monthOptions.push({ value: i, label: intToShortMonth(i)});
  }


  return(
    <div>
      <DropdownPicker
        options={monthOptions}
        selectedValue={selectedValue}
        onChange={onChange}
      />
    </div>
  )
}

export default MonthPicker;
